<?phpuse yii\helpers\Html;use yii\grid\GridView;/* @var $this yii\web\View *//* @var $searchModel frontend\models\HistoryClientsSearch *//* @var $dataProvider yii\data\ActiveDataProvider */$this->title = 'My History';$this->params['breadcrumbs'][] = $this->title;?><div class="history-clients-index">    <h1><?= Html::encode($this->title) ?></h1>    <?= $this->render('_searchHistory', ['model' => $searchModel]); ?>    <?= GridView::widget([        'dataProvider' => $dataProvider,//        'filterModel' => $searchModel,        'columns' => [            ['class' => 'yii\grid\SerialColumn'],            'id',            'user_name',            'book_name',            'autor_name',            'date',            'date_end',            'date_end',                        [                'attribute' => 'status_book',                'format' => 'raw',                'value' => function ($data) {                    if ($data->status_book == 1) {                        return Html::button('вернул', ['class' => 'btn btn-success btn-xs']);                    } else {                        return Html::button('не вернул', ['class' => 'btn btn-danger btn-xs']);                    }                },            ],//            ['class' => 'yii\grid\ActionColumn'],        ],    ]); ?></div>